import React from 'react';
import { StyleSheet, Button, View, SafeAreaView, Text, Alert } from 'react-native';
import { WebView } from 'react-native-webview';



const App = () => (
  <SafeAreaView style={styles.container}>
    <AreaPdf />
    <View >
      <View>
        <View style={styles.fixToText}>
          <Button
            title="anterior"
            color='black'
            borderRadius='10'
            padding='10px'
            onPress={() => Alert.alert('anterior')}
          />

          <Button style={styles.fixToText2}
            title="1"
            color='black'
            borderRadius='10'
            justifyContent='center'

            onPress={() => Alert.alert('Proximo')}
          />


          <Button
            title="Proximo"
            color='black'
            borderRadius='10'
            padding='10px'
            onPress={() => Alert.alert('Proximo')}
          />
        </View>
      </View>
    </View>
  </SafeAreaView>
);

const AreaPdf = () => {
  return (
    <View style={styles.container}>
      <PDF style={styles.container} />
    </View>

  );
}

const PDF = () => {


  return <View style={styles.fundo}>
    <iframe src="https://drive.google.com/viewerng/viewer?embedded=true&url=%7Byour%20pdf%20url%7D%E2%80%99"

      min-width="300px"
      height="100%"
      borderRadius="5"
      position="absolute"
      >

      </iframe>






  </View>
}

const Separator = () => (
  <View style={styles.separator} />
);








//estilo 

const styles = StyleSheet.create({
  App: {
    backgroundColor: "black",
  },
  container: {
    flex: '1',
    height: "100",
    padding: 12,
    justifyContent: "center",
    borderRadius: '10'

  },

  fixToText: {
    flex: 1,
    flexDirection: 'row',
    justifyContent: 'space-between',
    padding: 12,
  },

  fixToText2: {

    flexDirection: 'row',
    justifyContent: 'center',
    padding: 40,
  },
  separator: {
    marginVertical: 8,
    borderBottomColor: '#ccc2bd',
    borderBottomWidth: StyleSheet.hairlineWidth,
  },




});

export default App;
