import React from 'react';
import { StyleSheet, Button, View, SafeAreaView, Text, Alert } from 'react-native';
import { WebView } from 'react-native-webview';
import { withFormik } from 'formik';


const App = () => (
  <SafeAreaView style={styles.container}>
    <AreaPdf />

    <View >
      <View>
        <View style={styles.fixToText}>
          <Button
            title="anterior"
            onPress={() => Alert.alert('anterior')}
          />
          <Button
            title="Proximo"
            onPress={() => Alert.alert('Proximo')}
          />
        </View>
      </View>
    </View>
  </SafeAreaView>
);

const AreaPdf = () => {
  return (
    <View style={styles.container}>
      <View style={styles.box} />
      <PDF style={styles.container} />
      <Formulario style = {styles.container} />

    </View>

  );
}

const PDF = () => {


  return <View>
    <Button
      style={styles.box}
      title="VER PDF"
      onPress={() => Alert.alert('VER PDF')}

    ></Button>
    <WebView source={{ uri: 'https://drive.google.com/file/d/10V7DiWJqyWN2VxbTonpy7uk2r0zjMX3m/preview' }}
      style={{
        marginTop: 200,
        justifyContent: 'center',
        backgroundColor: 'red',
        height:20 
      }}

    />


  </View>
}

const Formulario = (props) =>{
  return (
  <View style={styles.container}>
  <TextInput
    value={props.values.email}
    onChangeText={text => props.setFieldValue('email', text)}
  />

  <TextInput
    value={props.values.password}
    onChangeText={text => props.setFieldValue('password', text)}
  />

  <Button
    onPress={props.handleSubmit}
    title="Login"
  />
</View>

);

  }






