const styles = StyleSheet.create({
  container: {
    flex: 1,
    height: 10,
    padding: 20,
    justifyContent: 'space-around',
    marginHorizontal: 16,

  },
  title: {
    textAlign: 'center',
    marginVertical: 8,
  },
  fixToText: {
    flex: 1,
    flexDirection: 'row',
    justifyContent: 'space-between',
    padding: 40,
  },
  separator: {
    marginVertical: 8,
    borderBottomColor: '#737373',
    borderBottomWidth: StyleSheet.hairlineWidth,
  },
  box: {
    flex: 1,
    backgroundColor: 'gray',
    height: '100',
    borderRadius: '10'

  },

  Button: {
    flex: 1,
    flexDirection: 'column',

  }

});

export default App;
