import React from 'react';
import { StyleSheet, Button, View, SafeAreaView, Text, Alert } from 'react-native';
import { WebView } from 'react-native-webview';



const App = () => (
  <SafeAreaView style={styles.container}>
    <AreaPdf />
    <View >
      <View>
        <View style={styles.fixToText}>
          <Button
            title="anterior"
            color='black'
            borderRadius='10'
            padding='10px'
            onPress={() => Alert.alert('anterior')}
          />
          <buttonsNunbers />
          <Button
            title="Proximo"
            color='black'
            borderRadius='10'
            padding='10px'
            onPress={() => Alert.alert('Proximo')}
          />
        </View>
      </View>
    </View>
  </SafeAreaView>
);

const buttonsNunbers = () => {
  return (
    <View>

      <Button style={styles.fixToText2}
        title="1"
        color='black'
        borderRadius='10'
        onPress={() => Alert.alert('Proximo')}
      />

      <Button style={styles.fixToText2}
        title="2"
        color='black'
        borderRadius='10'
        onPress={() => Alert.alert('Proximo')}
      />
    </View>
  );
}

const AreaPdf = () => {
  return (
    <View style={styles.container}>
      <View style={styles.box} />
      <PDF style={styles.container} />
    </View>

  );
}

const PDF = () => {


  return <View>
    <Button
      style={styles.ButtonVerPDF}
      title="VER PDF"
      color='black'
      fontFamily='black'
      borderRadius='10'

      onPress={() => Alert.alert('VER PDF')}

    ></Button>
    <WebView source={{ uri: 'https://drive.google.com/file/d/10V7DiWJqyWN2VxbTonpy7uk2r0zjMX3m/preview' }}
      style={{
        marginTop: 200,
        justifyContent: 'center',
        backgroundColor: 'red',
        height: 20
      }}

    />


  </View>
}







